import randomclass DetermineWhichProblem:        def __init__(self):        pass            def run(self):                list_of_parameter = ['A', 'B', 'C', 'D', 'E']        random_series = [random.randint(0,1) for _ in range(5)]         #print(random_series)        l = []        for i in range(len(random_series)):            if random_series[i] == 1:                #print(list_of_parameter[i])                l.append(list_of_parameter[i])        return l, random_seriesif __name__ == "__main__":    a = DetermineWhichProblem()    a = a.run()    print(a)